data/pipeline/stdEnv.inc
data/pipeline/stdView.inc
data/pipeline/stdObject.inc
sgl_view
sgl_obj
vec3 in_pos
vec3 in_nrm
vec2 in_uv0


layout (location = 0) in vec3 in_pos;
layout (location = 3) in vec3 in_nrm;
layout (location = 6) in vec2 in_uv0;

out block
{
    vec4 posn;
    vec3 nrm;
    vec2 uv0;
} sgl_out;

void main()
{
    sgl_out.posn = m * vec4(in_pos, 1);
    sgl_out.nrm = in_nrm;
    sgl_out.uv0 = in_uv0;
    gl_Position = p * v * m * vec4(in_pos, 1);
}

FRAGMENT
data/pipeline/stdEnv.inc
data/pipeline/stdView.inc
data/pipeline/stdMaterial.inc
sgl_view
sgl_mtl
vec4 out_rgba0


uniform sampler2D floorTexture;
vec3 lightPos = vec3(100, 100, 0);
int blinn = 1;

in block
{
    vec4 posn;
    vec3 nrm;
    vec2 uv0;
} sgl_in;

out vec4 out_rgba0;

void main (void)
{ 
    vec3 color = vec3(0.5,0.5,0.5);//texture(floorTexture, sgl_in.uv0).rgb;

    // ambient
    vec3 ambient = 0.05 * color;

    // diffuse
    vec3 lightDir = normalize(lightPos - vec3(sgl_in.posn));
    vec3 normal = normalize(sgl_in.nrm);
    float diff = max(dot(lightDir, normal), 0.0);
    vec3 diffuse = diff * color;

    // specular
    vec3 viewDir = vec3(normalize(viewPos - sgl_in.posn));
    vec3 reflectDir = reflect(-lightDir, normal);
    float spec = 0.0;

    if(blinn == 1)
    {
        vec3 halfwayDir = normalize(lightDir + viewDir);  
        spec = pow(max(dot(normal, halfwayDir), 0.0), 32.0);
    }
    else
    {
        vec3 reflectDir = reflect(-lightDir, normal);
        spec = pow(max(dot(viewDir, reflectDir), 0.0), 8.0);
    }

    vec3 specular = vec3(0.3) * spec; // assuming bright white light color
    out_rgba0 = vec4(ambient + diffuse + specular, 1.0);
}
